// components/Auth/LoginForm.js
import React, { useState } from 'react';
import BtnPrimary from '../BtnPrimary';

const LoginForm = ({ onLogin }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you would call your login API and handle authentication.
    onLogin({ email, password });
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white shadow rounded">
      <h2 className="text-2xl font-bold mb-4 text-center">Login</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Email"
          className="w-full p-2 border rounded mb-4"
          required
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
          className="w-full p-2 border rounded mb-4"
          required
        />
        <BtnPrimary type="submit">Login</BtnPrimary>
      </form>
      <div className="mt-4 flex space-x-4">
        <button className="flex-1 bg-red-500 text-white p-2 rounded">Login with Google</button>
        <button className="flex-1 bg-gray-800 text-white p-2 rounded">Login with GitHub</button>
      </div>
      <div className="mt-4 text-center">
        <span className="text-sm text-gray-600">Don't have an account? <a href="/signup" className="text-blue-600">Sign up</a></span>
      </div>
    </div>
  );
};

export default LoginForm;
