// components/Auth/SignupForm.js
import React, { useState } from 'react';
import BtnPrimary from '../BtnPrimary';

const SignupForm = ({ onSignup }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [username, setUsername] = useState("");
  const [role, setRole] = useState("team"); // Default role: team member

  const handleSubmit = (e) => {
    e.preventDefault();
    // Call your signup API here.
    onSignup({ email, password, username, role });
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white shadow rounded">
      <h2 className="text-2xl font-bold mb-4 text-center">Sign Up</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Username"
          className="w-full p-2 border rounded mb-4"
          required
        />
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Email"
          className="w-full p-2 border rounded mb-4"
          required
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
          className="w-full p-2 border rounded mb-4"
          required
        />
        <select
          value={role}
          onChange={(e) => setRole(e.target.value)}
          className="w-full p-2 border rounded mb-4"
        >
          <option value="admin">Admin</option>
          <option value="manager">Manager</option>
          <option value="team">Team Member</option>
        </select>
        <BtnPrimary type="submit">Sign Up</BtnPrimary>
      </form>
      <div className="mt-4 text-center">
        <span className="text-sm text-gray-600">Already have an account? <a href="/login" className="text-blue-600">Login</a></span>
      </div>
    </div>
  );
};

export default SignupForm;
