import React, { useState, useEffect } from "react";
import { NavLink, useNavigate } from "react-router-dom";
import axios from "axios";
import toast from "react-hot-toast";
import AddProjectModal from "./AddProjectModal"; // Import the modal component

const Sidebar = () => {
  const [projects, setProjects] = useState([]);
  const [showProjects, setShowProjects] = useState(false);
  const [showTasks, setShowTasks] = useState(false);
  const [isModalOpen, setModalOpen] = useState(false); // State for modal
  const navigate = useNavigate();

  // Fetch projects from backend
  useEffect(() => {
    axios
      .get("http://localhost:9000/projects")
      .then((res) => setProjects(res.data))
      .catch(() => toast.error("Failed to load projects"));
  }, []);

  // Open the Create Project modal
  const handleOpenModal = () => {
    setModalOpen(true);
  };

  // Close the modal after project creation
  const handleCloseModal = () => {
    setModalOpen(false);
  };

  return (
    <>
      <aside className="h-full p-4 bg-gray-100">
        <ul>
          {/* Dashboard */}
          <li className="mb-2">
            <NavLink to="/" className="block p-2 hover:bg-gray-200 rounded">
              Dashboard
            </NavLink>
          </li>

          {/* Projects Option */}
          <li className="mb-2">
            <button
              onClick={() => setShowProjects((prev) => !prev)}
              className="w-full text-left p-2 hover:bg-gray-200 rounded flex items-center justify-between"
            >
              <span>Projects</span>
              <svg
                className={`w-4 h-4 transition-transform duration-200 ${
                  showProjects ? "rotate-180" : ""
                }`}
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M19 9l-7 7-7-7"
                />
              </svg>
            </button>
            {showProjects && (
              <ul className="mt-2 ml-4">
                {projects.length > 0 ? (
                  projects.map((project) => (
                    <li key={project.id} className="mb-1">
                      <button
                        onClick={() => navigate(`/${project.id}`)}
                        className="block p-2 hover:bg-gray-300 rounded text-left w-full"
                      >
                        {project.title}
                      </button>
                    </li>
                  ))
                ) : (
                  <li className="p-2 text-sm text-gray-500">
                    No projects available
                  </li>
                )}
                {/* "Create New Project" now opens the AddProjectModal */}
                <li className="mt-2">
                  <button
                    onClick={handleOpenModal}
                    className="block p-2 text-blue-600 hover:bg-blue-100 rounded text-left w-full"
                  >
                    + Create New Project
                  </button>
                </li>
              </ul>
            )}
          </li>

          {/* Tasks Option */}
          <li className="mb-2">
            <button
              onClick={() => setShowTasks((prev) => !prev)}
              className="w-full text-left p-2 hover:bg-gray-200 rounded flex items-center justify-between"
            >
              <span>Tasks</span>
              <svg
                className={`w-4 h-4 transition-transform duration-200 ${
                  showTasks ? "rotate-180" : ""
                }`}
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M19 9l-7 7-7-7"
                />
              </svg>
            </button>
            {showTasks && (
              <ul className="mt-2 ml-4">
                <li className="mb-1">
                  <NavLink
                    to="/tasks/my-tasks"
                    className="block p-2 hover:bg-gray-300 rounded"
                  >
                    My Tasks
                  </NavLink>
                </li>
                <li className="mb-1">
                  <NavLink
                    to="/tasks/team-tasks"
                    className="block p-2 hover:bg-gray-300 rounded"
                  >
                    Team Tasks
                  </NavLink>
                </li>
              </ul>
            )}
          </li>

          {/* Other Navigation Items */}
          <li className="mb-2">
            <NavLink
              to="/settings"
              className="block p-2 hover:bg-gray-200 rounded"
            >
              Settings
            </NavLink>
          </li>
        </ul>
      </aside>

      {/* Create Project Modal */}
      <AddProjectModal isModalOpen={isModalOpen} closeModal={handleCloseModal} />
    </>
  );
};

export default Sidebar;
